import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Observable } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { Institute } from '../models/institute';
import { Thesis } from '../models/thesis';
import { University } from '../models/university';
import { ThesisManagementSystemService } from '../services/thesisManagementSystem.service';


@Component({
  selector: 'app-add-thesis',
  templateUrl: './add-thesis.component.html',
  styleUrls: ['./add-thesis.component.scss'],
})
export class AddThesisComponent implements OnInit {
  public universities: University[] = [];
  public institutes: Institute[] = [];
  thesisForm: FormGroup;
  title = 'Create';
  thesisId: number = 0;
  errorMessage: any;
  submitted = false;



  constructor(
    private fb: FormBuilder,
    private avRoute: ActivatedRoute,
    private thesisManagementSystemService: ThesisManagementSystemService,
    private router: Router
  ) {

    this.thesisForm = this.fb.group({
      thesisId: 0,
      title: ['', [Validators.required]],
      universityId: 0,
      instituteId: 0,
      abstract: ['', [Validators.required]],

    });
  }



  ngOnInit(): void {
    this.getUniversities();

    this.avRoute.paramMap.subscribe((params: Params) => {
      this.thesisId = params.get('id');
    });

    if (this.thesisId > 0) {
      this.title = 'Edit';

      this.thesisManagementSystemService.getThesisData(this.thesisId).subscribe(
        (response: Thesis) => {
          this.getInstitutes(response.universityId);
          this.thesisForm.setValue(
            {
              thesisId: response.thesisId,
              title: response.title,
              universityId: response.universityId,
              instituteId: response.instituteId,
              abstract: response.abstract
            });
        },
        (error) => console.error(error)
      );

    }
  }

  onUniversitySelectChange(event: any) {
    // You can access the selected value using event.target.value
    const selectedValue = event.target.value;

    // Call your method or perform actions based on the selected value
    this.getInstitutes(selectedValue);
  }

  getUniversities(): void {
    this.thesisManagementSystemService
      .getUniversityList()
      .subscribe((universityData) => (this.universities = universityData));
  }

  getInstitutes(universityId: number): void {
    this.thesisManagementSystemService
      .getInstituteListForUniversity(universityId)
      .subscribe((instituteData) => (this.institutes = instituteData));
  }

  get registerFormControl() {
    return this.thesisForm.controls;
  }

  save(): void {
    this.submitted = true;
    if (!this.thesisForm.valid) {
      return;
    }

    if (this.thesisId > 0) {
      this.updateThesis();
    } else {
      this.addThesis();
    }
  }

  cancel(): void {
    this.navigateToFetchThesis();
  }

  private addThesis(): void {

    this.thesisManagementSystemService.saveThesis(this.thesisForm.value).subscribe(
      () => {
        this.navigateToFetchThesis();
      },
      (error) => console.error(error)
    );

  }

  private updateThesis(): void {
    this.thesisManagementSystemService.updateThesis(this.thesisForm.value).subscribe(
      () => {
        this.navigateToFetchThesis();
      },
      (error) => console.error(error)
    );
  }

  private navigateToFetchThesis() {
    this.router.navigate(['/fetch-thesis']);
  }
}
<h1>{{ title }}</h1>
<h3>Thesis</h3>
<hr />

<form [formGroup]="thesisForm" (ngSubmit)="save()">


    <div class="form-group row">
        <label class="control-label col-md-12">Title</label>
        <div class="col-md-4">
            <textarea ng-model="myTextarea" formControlName="title" style="width:415px;height:60px"></textarea>
        </div>
        <span class="text-danger" *ngIf="
        (registerFormControl.title.touched || submitted) &&
        registerFormControl.title.errors?.required
      ">
            Title is required.
        </span>
    </div>

    <div class="form-group row">
        <label class="control-label col-md-12">Abstract</label>
        <div class="col-md-4">
            <textarea ng-model="myTextarea" formControlName="abstract" style="width:415px;height:160px"></textarea>
        </div>
        <span class="text-danger" *ngIf="
        (registerFormControl.abstract.touched || submitted) &&
        registerFormControl.abstract.errors?.required
      ">
            Abstract is required.
        </span>
    </div>

    <div class="form-group row">
        <label class="control-label col-md-12" for="universityId">University</label>
        <div class="col-md-4">
            <select class="form-control" data-val="true" formControlName="universityId" (change)="onUniversitySelectChange($event)">
                <option value="">-- Select University --</option>
                <option *ngFor="let university of universities" value="{{ university.universityId }}">{{ university.universityName }}</option>
            </select>
        </div>
        <span class="text-danger" *ngIf="
        (registerFormControl.universityId.touched || submitted) &&
        registerFormControl.universityId.errors?.required
      ">
            University is required
        </span>
    </div>

    <div class="form-group row">
        <label class="control-label col-md-12" for="instituteId">Institute</label>
        <div class="col-md-4">
            <select class="form-control" data-val="true" formControlName="instituteId">
                <option value="">-- Select Institute --</option>
                <option *ngFor="let institute of institutes" value="{{ institute.instituteId }}">{{ institute.instituteName }}</option>
            </select>
        </div>
        <span class="text-danger" *ngIf="
        (registerFormControl.instituteId.touched || submitted) &&
        registerFormControl.instituteId.errors?.required
      ">
            Institute is required
        </span>
    </div>


    <br />
    <div class="form-group">
        <button type="submit" class="btn btn-primary">Save</button>
        &nbsp;
        <button class="btn btn-danger" (click)="cancel()">Cancel</button>
    </div>
</form>
