import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { University } from '../models/university';
import { Comment } from '../models/comment';
import { Observable } from 'rxjs';
import { HttpHeaders } from '@angular/common/http';


let hostUrl = 'ThesisManagementSystem/';


@Injectable({
  providedIn: 'root'
})
export class ThesisManagementSystemService {

  constructor(private http: HttpClient) { }

  getUniversityList(): Observable<University[]> {
    return this.http.get<University[]>(hostUrl + 'GetUniversityList');
  }

  getUniversityData(id: number): Observable<University> {
    return this.http.get<University>(hostUrl + 'GetUniversity/' + id);
  }

  saveUniversity(task: University): Observable<any> {
    return this.http.post(hostUrl + 'AddUniversity', task);
  }

  updateUniversity(task: University): Observable<any> {
    return this.http.put(hostUrl + 'UpdateUniversity', task);
  }

  deleteUniversity(id: number): Observable<any> {
    return this.http.delete(hostUrl + 'DeleteUniversity/' + id);
  }

  getCommentList(taskId: number): Observable<Comment[]> {
    return this.http.get<Comment[]>(hostUrl + 'GetCommentList/' + taskId);
  }

  getCommentData(id: number): Observable<Comment> {
    return this.http.get<Comment>(hostUrl + 'GetComment/' + id);
  }

  saveComment(comment: Comment): Observable<any> {
    return this.http.post(hostUrl + 'AddComment', comment);
  }

  updateComment(comment: Comment): Observable<any> {
    return this.http.put(hostUrl + 'UpdateComment', comment);
  }

  deleteComment(id: number): Observable<any> {
    return this.http.delete(hostUrl + 'DeleteComment/' + id);
  }
}
